    /**
     * Transforms a ResultSet iterating on the tran_type table on a TranTypeBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting TranTypeBean bean
     * @throws DAOException
     */
    //30
    public TranTypeBean decodeRow(ResultSet rs, int[] fieldList) throws DAOException
    {
        TranTypeBean bean = this.createTranTypeBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case ID_LAST_UPDATE:
                        ++pos;
                        bean.setLastUpdate(rs.getTimestamp(pos));
                        break;
                    case ID_DESCRIPTION:
                        ++pos;
                        bean.setDescription(rs.getString(pos));
                        break;
                    case ID_TRAN_TYPE:
                        ++pos;
                        bean.setTranType(rs.getString(pos));
                        break;
                    case ID_TRAN_TYPE_ID:
                        ++pos;
                        bean.setTranTypeId(Manager.getInteger(rs, pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

